"""
212. Word Search II
Hard

2954

126

Add to List

Share
Given a 2D board and a list of words from the dictionary, find all words in the board.

Each word must be constructed from letters of sequentially adjacent cell, where "adjacent" cells are those horizontally or vertically neighboring. The same letter cell may not be used more than once in a word.



Example:

Input:
board = [
  ['o','a','a','n'],
  ['e','t','a','e'],
  ['i','h','k','r'],
  ['i','f','l','v']
]
words = ["oath","pea","eat","rain"]

Output: ["eat","oath"]


Note:

All inputs are consist of lowercase letters a-z.
The values of words are distinct.
"""

from typing import List

class Solution0:
    def findWords(self, board: List[List[str]], words: List[str]) -> List[str]:
        if not board or not board[0]:
            return []
        nRow = len(board)
        nCol = len(board[0])
        cnt_board = {}
        for i in range(nRow):
            for j in range(nCol):
                if board[i][j] not in cnt_board:
                    cnt_board[board[i][j]] = 1
                else:
                    cnt_board[board[i][j]] += 1
        excluded = []
        for word in words:
            if len(word) > nRow * nCol:
                excluded.append(word)
            else:
                cnt_word = {}
                for i in range(len(word)):
                    if word[i] not in cnt_word:
                        cnt_word[word[i]] = 1
                    else:
                        cnt_word[word[i]] += 1
                for ch in cnt_word.keys():
                    if ch not in cnt_board or cnt_word[ch] > cnt_board[ch]:
                        excluded.append(word)
                        break
        # print(excluded)

        def backtracking(row, col, k):
            # start from position (row, col), looking for k-th letter in word
            if k == len(word):
                return True
            for (i, j) in [(row + r, col + c) for (r, c) in [(0, 1), (0, -1), (1, 0), (-1, 0)]]:
                if 0 <= i < nRow and 0 <= j < nCol and board[i][j] == word[k] and (i, j) not in used:
                    used.append((i, j))
                    if backtracking(i, j, k + 1):
                        return True
                    used.pop()
            return False

        ans = []
        for word in words:
            if word not in excluded:
                rev = 0
                if cnt_board[word[-1]] < cnt_board[word[0]]:
                    rev = 1
                    word = word[::-1]

                found, used, i, j = False, [], 0, 0
                while not found and i < nRow:
                    for j in range(nCol):
                        if board[i][j] == word[0]:
                            used.append((i, j))
                            if backtracking(i, j, 1):
                                if rev:
                                    word = word[::-1]
                                ans.append(word)
                                found = True
                                break
                            used.pop()
                    i += 1
        return ans


class Solution:
    def findWords(self, board, words):
        def dfs(i, j, parent):
            letter = board[i][j]
            node = parent[letter]

            if '$' in node:
                ret.append(node.pop('$'))

            board[i][j] = ''

            if i > 0 and board[i - 1][j] in node:
                dfs(i - 1, j, node)
            if j + 1 < n and board[i][j + 1] in node:
                dfs(i, j + 1, node)
            if i + 1 < m and board[i + 1][j] in node:
                dfs(i + 1, j, node)
            if j > 0 and board[i][j - 1] in node:
                dfs(i, j - 1, node)

            board[i][j] = letter

            if not node:
                parent.pop(letter)

        root = {}
        for word in words:
            curr = root
            for letter in word:
                if letter not in curr:
                    curr[letter] = {}

                curr = curr[letter]
            curr['$'] = word

        ret = []
        m, n = len(board), len(board[0])

        for i in range(m):
            for j in range(n):
                if board[i][j] in root:
                    dfs(i, j, root)

        return ret


